      return false;
    }

    return true;
  });


  return(
    <>
     <Header/>
     <div>
    <img src="image.jpg"  alt="images"></img> 
    </div>
    <form>
    <div>
        <input type="text" value={searchQuery}  placeholder="Search..." />
