import React, { useState } from 'react';
import { FiChevronDown, FiSearch } from 'react-icons/fi';
import { css } from '@emotion/react';
import axios from 'axios';

const AccordionPage = () => {
  const [accordionItems, setAccordionItems] = useState([]);
  const [activeIndex, setActiveIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterAlphabet, setFilterAlphabet] = useState('');

  // Fetch the accordion data from an API or define it locally
  useEffect(() => {
    axios.get('https://api.example.com/accordion-data')
      .then(response => {
        setAccordionItems(response.data);
      })
      .catch(error => {
        console.error('Error fetching accordion data:', error);
      });
  }, []);

  const filteredItems = accordionItems.filter(item => {
    const searchMatch = item.content.toLowerCase().includes(searchQuery.toLowerCase());
    const alphabetMatch = item.title.toLowerCase().startsWith(filterAlphabet.toLowerCase());

    return searchMatch && alphabetMatch;
  });

  const onItemClick = index => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const onSearchChange = e => {
    setSearchQuery(e.target.value);
  };

  const onAlphabetFilterChange = alphabet => {
    setFilterAlphabet(alphabet);
  };

  return (
    <div css={pageStyles}>
      <div css={searchContainerStyles}>
        <input
          type="text"
          value={searchQuery}
          onChange={onSearchChange}
          placeholder="Search..."
          css={searchInputStyles}
        />
        <FiSearch css={searchIconStyles} />
      </div>

      <div css={alphabetFilterStyles}>
        <button onClick={() => onAlphabetFilterChange('A')}>A</button>
        <button onClick={() => onAlphabetFilterChange('B')}>B</button>
        {/* Add other alphabet buttons as needed */}
      </div>

      {filteredItems.map((item, index) => (
        <div key={index} css={accordionItemStyles}>
          <div
            css={titleStyles(activeIndex === index)}
            onClick={() => onItemClick(index)}
          >
            {item.title}
            <FiChevronDown css={chevronStyles(activeIndex === index)} />
          </div>
          {index === activeIndex && (
            <div css={contentStyles}>
              {item.content}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};
