import { Accordion } from "react-bootstrap"
import classes from './Accordian.module.css'
const Accordian = () =>{

  const accordionData = [
    {
      title: "What is React #1",
      content:
      "React is a popular JavaScript library used for building user interfaces (UI) for web applications. It was developed by Facebook and released as an open-source project. React allows developers to create reusable UI components that efficiently update and render based on changes in application data.  Key features and concepts of React include:Component-Based Architecture: React follows a component-based architecture, where the user interface is divided into reusable and independent components. Each component encapsulates its own logic and state, making it easier to manage and maintain complex UI structures."
    },
    {
      title: "Components in React #2",
      content: "Components in React are...",
    },
   
  ];
    return(
        <div>
          <Accordion defaultActiveKey="0" className={classes.accordion}>
        {accordionData.map((item, index) => (
          <Accordion.Item key={index} eventKey={index.toString()}>
            <Accordion.Header>{item.title}</Accordion.Header>
            <Accordion.Body>{item.content}</Accordion.Body>
          </Accordion.Item>
        ))}
      </Accordion>
        </div>



        
           

    )
}

export default Accordian
