import React, { useState } from 'react';

const Accordion = ({ items }) => {
  const [activeIndex, setActiveIndex] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [filterAlphabet, setFilterAlphabet] = useState('');

  const filteredItems = items.filter(item => {
    const searchMatch = item.content.toLowerCase().includes(searchQuery.toLowerCase());
    const alphabetMatch = item.title.toLowerCase().startsWith(filterAlphabet.toLowerCase());

    return searchMatch && alphabetMatch;
  });

  const onItemClick = index => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const onSearchChange = e => {
    setSearchQuery(e.target.value);
  };

  const onAlphabetFilterChange = alphabet => {
    setFilterAlphabet(alphabet);
  };

  return (
    <div>
      <input type="text" value={searchQuery} onChange={onSearchChange} placeholder="Search..." />

      <div className="alphabet-filter">
        <button onClick={() => onAlphabetFilterChange('A')}>A</button>
        <button onClick={() => onAlphabetFilterChange('B')}>B</button>
        {/* Add other alphabet buttons as needed */}
      </div>

      {filteredItems.map((item, index) => (
        <div key={index}>
          <div className={`accordion-title ${index === activeIndex ? 'active' : ''}`} onClick={() => onItemClick(index)}>
            {item.title}
          </div>
          {index === activeIndex && <div className="accordion-content">{item.content}</div>}
        </div>
      ))}
    </div>
  );
};

const App = () => {
  const accordionItems = [
    { title: 'Apple', content: 'This is the content for Apple.' },
    { title: 'Banana', content: 'This is the content for Banana.' },
    { title: 'Cherry', content: 'This is the content for Cherry.' },
    // Add more items here
  ];

  return (
    <div>
      <h1>Accordion Page</h1>
      <Accordion items={accordionItems} />
    </div>
  );
};

export default App;
