import React, { Fragment } from "react";
 import  ReactDOM  from "react-dom";
 import classes from './Modal.module.css'

   const Overlay = (props) =>{

    return (
     <div className={classes.modal}>
        <div>quantity</div>
        <span>Add Item</span>
         <div className={classes.content}>{props.children}</div>
         <button>Order</button>
         <button onClick={props.closeCart}>Close</button>
     </div>
    )
  }
  const holder = document.getElementById('modal')

  const Modal = (props) =>{
    return(
     <Fragment>
         {ReactDOM.createPortal(<Overlay onClick={props.closeCart}>{props.children}</Overlay>,holder)}
    </Fragment>
    )
  }

  export default Modal
