const Overlay = () =>{
    <div>My Modal</div>
}
const holder = document.getElementById('modal')


  const Modal = (props) =>{
    return(
        <Fragment>
        {ReactDOM.createPortal(<Overlay>{props.children}</Overlay>,holder)}
        </Fragment>
    )
}

export default Modal
