import { createSlice } from "@reduxjs/toolkit";

 const initialState = {isCart:false}
const authSlice = createSlice({
    name:"Auth",
    initialState,
    reducers :{
        setCart(state){
            state.isCart = true
          },
          closeCart(state){
              state.isCart= false
          },
    }
})

export default authSlice
export const uiActions = authSlice.actions
