//import React from "react";
import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialState = {isCart:false, items : [], totalQuantity:0}
const cartStore = createSlice({
    name:'CART',
    initialState,
    reducers :{
        setCart(state){
          state.isCart = true
        },
        closeCart(state){
            state.isCart= false
        },
        addItemToCart(state,action){
          const newItems = action.payload
          const existingItem = state.items.find((item) =>item.id===newItems.id)
          state.totalQuantity++
          if(!existingItem){
            state.items.push({
              itemId : newItems.id,
              price: newItems.price,
              quantity:1,
              totalPrice: newItems.price,
              name:newItems.title
            })
          }
          else {
            existingItem.quantity++
            existingItem.totalPrice = existingItem.totalPrice + newItems.price;
          }

        },
        removeItemFromCart(state,action){
             const id = action.payload
             const existingItem = state.items.find((item) => item.id ===id)
             state.totalQuantity--
             if(existingItem.quantity===1){
                 state.items = state.items.filter(item => item.id!==id)
             }
             else{
              existingItem.quantity--
             }
        }
    }

})

const store = configureStore({
  reducer : cartStore.reducer
})

export default store

export const cartState = cartStore.actions
